package com.sws.dao;import java.util.List;import com.gk.extend.hibernate.dao.IEntityDao;import com.sws.model.StaffInfo;public interface StaffInfoDao extends IEntityDao<StaffInfo> {	public List<StaffInfo> findByDepartAndType(String departIds,Long departId,List<String> types);	public List<StaffInfo> findByDepartAndTypeAndName(String departIds,Long departId,List<String> types,String staffName);	public List<StaffInfo> findByNameAndType(String departIds,Long departId,String name,List<String> types);	public List<StaffInfo> findByDepart(List<String> manageDepart,String curDepartId);	public List<StaffInfo> findByNameAndTypeAndDLevel(			List<String> manageDepart, String departIds, Long departId,			String name, List<String> types);	public List<StaffInfo> findByDepartAndTypeAndNameAndDLevel(			List<String> manageDepart, String departIds, Long departId,			List<String> types, String staffName);	public List<StaffInfo> findByDepartAndTypeAndDlevel(			List<String> manageDepart, String departIds, Long departId,			List<String> types);		/**	 * 通过员工编号查找员工（为空时可能有多个员工，所以用list）	 * @param staffNo	 * @return	 * @author wwg	 */	List<StaffInfo> findByStaffNo(String staffNo);		/**	 * 通过胸牌的rfid查询员工的name	 * 其中需要借助device表	 * @param rfid	 * @return	 * @author wwg	 */	String findNameByRfid(String rfid);		/**	 * 通过departId（group_tree）查询该科室下的员工	 * @param departmentId	 * @return	 * @author wwg	 */	List<StaffInfo> findByDepartmentId(Long departmentId);		/**	 * 通过departId(group_tree)查询该科室下的某类型的员工	 * @param category	 * @param departmentId	 * @return	 * @author wwg	 */	List<StaffInfo> findByDepartmentIdAndStaffType(String category, Long departmentId);	}