package com.sws.service.imp;import java.util.ArrayList;import java.util.List;import org.springframework.beans.factory.annotation.Autowired;import com.gk.extend.hibernate.entity.QueryEntity;import com.sws.dao.ApSetDao;import com.sws.model.ApSet;import com.sws.service.ApSetService;import com.sys.core.util.CollectionUtils;import com.sys.core.util.StringUtils;import com.sys.core.util.bean.Page;//@Service("roleInfoService")public class ApSetServiceImpl implements ApSetService {    	@Autowired    private ApSetDao apSetDao;	@Override	public ApSet getById(Long id) {		// TODO Auto-generated method stub		return apSetDao.get(id);	}	@Override	public void save(ApSet info) {		// TODO Auto-generated method stub		apSetDao.save(info);	}	@Override	public void update(ApSet info) {		// TODO Auto-generated method stub		apSetDao.update(info);	}	@Override	public void deleteAll(String ids) {		// TODO Auto-generated method stub		if (StringUtils.isNotBlank(ids)) {            List<Long> idList = CollectionUtils.toLongList(ids);            if (CollectionUtils.isNotEmpty(idList)) {                for (Long id : idList) {                	apSetDao.deleteById(id);                }            }        }	}	@Override	public Page page(QueryEntity queryEntity, int start, int limit,String sortname,String sortorder) {		// TODO Auto-generated method stub		List<String> paramList = new ArrayList<String>();		List<Object> valueList = new ArrayList<Object>();		if(StringUtils.isNotBlank(queryEntity.getStr1())){			paramList.add("no");			valueList.add(queryEntity.getStr1());		}		 Page page = apSetDao.page(paramList.toArray(new String[paramList.size()]), valueList.toArray(),start, limit, sortname,				 sortorder.toLowerCase().equals("asc")?true:false);		return page;	}	}