package com.sws.service.imp;import java.util.ArrayList;import java.util.List;import org.springframework.beans.factory.annotation.Autowired;import com.gk.extend.hibernate.entity.QueryEntity;import com.sws.dao.MenuRoleDao;import com.sws.model.MenuRole;import com.sws.service.MenuRoleService;import com.sys.core.util.CollectionUtils;import com.sys.core.util.StringUtils;import com.sys.core.util.bean.Page;//@Service("menuRoleService")public class MenuRoleServiceImpl implements MenuRoleService {    	@Autowired    private MenuRoleDao menuRoleDao;	@Override	public MenuRole getById(Long id) {		// TODO Auto-generated method stub		return menuRoleDao.get(id);	}	@Override	public void save(MenuRole info) {		// TODO Auto-generated method stub		menuRoleDao.save(info);	}	@Override	public void update(MenuRole info) {		// TODO Auto-generated method stub		menuRoleDao.update(info);	}	@Override	public void deleteAll(String ids) {		// TODO Auto-generated method stub		if (StringUtils.isNotBlank(ids)) {            List<Long> idList = CollectionUtils.toLongList(ids);            if (CollectionUtils.isNotEmpty(idList)) {                for (Long id : idList) {                	menuRoleDao.deleteById(id);                }            }        }	}	@Override	public Page page(QueryEntity queryEntity, int start, int limit,String sortname,String sortorder) {		// TODO Auto-generated method stub		List<String> paramList = new ArrayList<String>();		List<Object> valueList = new ArrayList<Object>();		if(StringUtils.isNotBlank(queryEntity.getStr1())){			paramList.add("no");			valueList.add(queryEntity.getStr1());		}		if(StringUtils.isNotBlank(queryEntity.getStr2())){			paramList.add("name");			valueList.add(queryEntity.getStr2());		}		 Page page = menuRoleDao.page(paramList.toArray(new String[paramList.size()]), valueList.toArray(),start, limit, sortname,				 sortorder.toLowerCase().equals("asc")?true:false);		return page;	}}