package com.sws.service.imp;import java.util.ArrayList;import java.util.List;import org.springframework.beans.factory.annotation.Autowired;import com.gk.extend.hibernate.entity.QueryEntity;import com.sws.dao.NoticeInfoDao;import com.sws.model.NoticeInfo;import com.sws.service.NoticeInfoService;import com.sys.core.util.CollectionUtils;import com.sys.core.util.StringUtils;import com.sys.core.util.bean.Page;//@Service("roleInfoService")public class NoticeInfoServiceImpl implements NoticeInfoService {    	@Autowired    private NoticeInfoDao noticeInfoDao;	@Override	public NoticeInfo getById(Long id) {		// TODO Auto-generated method stub		return noticeInfoDao.get(id);	}	@Override	public void save(NoticeInfo info) {		// TODO Auto-generated method stub		noticeInfoDao.save(info);	}	@Override	public void update(NoticeInfo info) {		// TODO Auto-generated method stub		noticeInfoDao.update(info);	}	@Override	public void deleteAll(String ids) {		// TODO Auto-generated method stub		if (StringUtils.isNotBlank(ids)) {            List<Long> idList = CollectionUtils.toLongList(ids);            if (CollectionUtils.isNotEmpty(idList)) {                for (Long id : idList) {                	noticeInfoDao.deleteById(id);                }            }        }	}	@Override	public Page page(QueryEntity queryEntity, int start, int limit,String sortname,String sortorder) {		// TODO Auto-generated method stub		List<String> paramList = new ArrayList<String>();		List<Object> valueList = new ArrayList<Object>();		if(StringUtils.isNotBlank(queryEntity.getStr1())){			paramList.add("no");			valueList.add(queryEntity.getStr1());		}		if(StringUtils.isNotBlank(queryEntity.getStr2())){			paramList.add("name");			valueList.add(queryEntity.getStr2());		}		 Page page = noticeInfoDao.page(paramList.toArray(new String[paramList.size()]), valueList.toArray(),start, limit, sortname,				 sortorder.toLowerCase().equals("asc")?true:false);		return page;	}	}