package com.sws.service.imp;import java.util.ArrayList;import java.util.List;import org.springframework.beans.factory.annotation.Autowired;import com.gk.extend.hibernate.entity.QueryEntity;import com.sws.dao.ParameterInfoDao;import com.sws.model.ParameterInfo;import com.sws.service.ParameterInfoService;import com.sys.core.util.CollectionUtils;import com.sys.core.util.StringUtils;import com.sys.core.util.bean.Page;//@Service("roleInfoService")public class ParameterInfoServiceImpl implements ParameterInfoService {    	@Autowired    private ParameterInfoDao parameterInfoDao;	@Override	public ParameterInfo getById(Long id) {		// TODO Auto-generated method stub		return parameterInfoDao.get(id);	}	@Override	public void save(ParameterInfo info) {		// TODO Auto-generated method stub		parameterInfoDao.save(info);	}	@Override	public void update(ParameterInfo info) {		// TODO Auto-generated method stub		parameterInfoDao.merge(info);	}	@Override	public void deleteAll(String ids) {		// TODO Auto-generated method stub		if (StringUtils.isNotBlank(ids)) {            List<Long> idList = CollectionUtils.toLongList(ids);            if (CollectionUtils.isNotEmpty(idList)) {                for (Long id : idList) {                	parameterInfoDao.deleteById(id);                }            }        }	}	@Override	public Page page(QueryEntity queryEntity, int start, int limit,String sortname,String sortorder) {		// TODO Auto-generated method stub		if(sortname.equals("typeName")){			sortname="type";		}		List<String> paramList = new ArrayList<String>();		List<Object> valueList = new ArrayList<Object>();		if(StringUtils.isNotBlank(queryEntity.getStr1())){			paramList.add("key");			valueList.add(queryEntity.getStr1());		}		 Page page = parameterInfoDao.page(paramList.toArray(new String[paramList.size()]), valueList.toArray(),start, limit, sortname,				 sortorder.toLowerCase().equals("asc")?true:false);		return page;	}	@Override	public boolean saveOrUpdateAll(List<ParameterInfo> list) {		// TODO Auto-generated method stub		return parameterInfoDao.saveOrUpdateAll(list);	}	@Override	public ParameterInfo findByTypeAndKey(Integer type, String key) {		// TODO Auto-generated method stub		return parameterInfoDao.findByTypeAndKey(type, key);	}	@Override	public List<ParameterInfo> findByType(Integer type) {		// TODO Auto-generated method stub		return parameterInfoDao.findByType(type);	}	}