package com.sws.service.imp;import java.util.List;import org.springframework.beans.factory.annotation.Autowired;import com.gk.extend.hibernate.entity.QueryEntity;import com.sws.dao.PreRateStatisticsDao;import com.sws.model.PreRateStatistics;import com.sws.service.PreRateStatisticsService;import com.sys.core.util.CollectionUtils;import com.sys.core.util.StringUtils;//@Service("staffInfoService")public class PreRateStatisticsServiceImpl implements PreRateStatisticsService {    	@Autowired    private PreRateStatisticsDao preRateStatisticsDao;	@Override	public PreRateStatistics getById(Long id) {		// TODO Auto-generated method stub		return preRateStatisticsDao.get(id);	}	@Override	public void save(PreRateStatistics info) {		// TODO Auto-generated method stub		preRateStatisticsDao.save(info);	}	@Override	public void update(PreRateStatistics info) {		// TODO Auto-generated method stub		preRateStatisticsDao.update(info);	}	@Override	public void deleteAll(String ids) {		// TODO Auto-generated method stub		if (StringUtils.isNotBlank(ids)) {            List<Long> idList = CollectionUtils.toLongList(ids);            if (CollectionUtils.isNotEmpty(idList)) {                for (Long id : idList) {                	preRateStatisticsDao.deleteById(id);                }            }        }	}	@Override	public void saveAll(List<PreRateStatistics> list) {		// TODO Auto-generated method stub		preRateStatisticsDao.saveAll(list);	}	@Override	public List<PreRateStatistics> findBy(String[] propertyName, Object[] value) {		// TODO Auto-generated method stub		return preRateStatisticsDao.findBy(propertyName,value);	}	@Override	public List<PreRateStatistics> queryList(QueryEntity queryEntity) {		// TODO Auto-generated method stub		return preRateStatisticsDao.queryList(queryEntity);	}}